import { Component, Input } from '@angular/core';
import { Cart } from '../cart';

@Component({
  selector: 'app-cart',
  templateUrl: './cart.component.html',
  styleUrls: ['./cart.component.css']
})
export class CartComponent {
  @Input() breadIn!: Cart
}
addBread(bread: Cart){
  this.breadIn.push(bread);
  this.isAddBread = false;
}
addNewBread(){
  this.isAddBread = true;
}
<p>cart works!</p>
