import { Component, Output, EventEmitter } from '@angular/core';
import {FormControl, FormGroup} from '@angular/forms';

@Component({
  selector: 'app-sign-in',
  templateUrl: './sign-in.component.html',
  styleUrls: ['./sign-in.component.css']
})
export class SignInComponent {
  ngOnInit() {
    this.signInForm.reset();
  }
  @Output()userOut = new EventEmitter();
  signInForm= new FormGroup({
    name: new FormControl(''),
    username: new FormControl(''),
    age: new FormControl(''),
    address: new FormControl(''),
    password: new FormControl('')
  })
  addUser() {
    this.userOut.emit(this.signInForm.value);
  }


}
<body>

  <h3 class="dv">SIGN IN</h3>
  <form [formGroup]="signInForm" (ngSubmit)="addUser()" class="dv">
    <label for="name">User Name</label>
    <br>
    <input id="name" type="text" formControlName="name">
    <br><br>
    <label for="name">User Type</label>
    <br>
    <form id="form1">
      <select width=300
              style="width: 450px">
        <option value='blue'>Admin</option>
        <option value='green'>Baker</option>
        <option value='red'>Customer</option>
        <option value='' selected>Select Type of User</option>
      </select>
    </form>
    <br><br>
    <label for="pwd">Password</label>
    <br>
    <input type="password" id="pwd" formControlName="pwd">
    <br><br>
  <button>Sign Up</button>
  <br><br>
  <button >Sign In</button>
  <br>
  </form>
  </body>
