import { Component } from '@angular/core';
import { SignUp } from './sign-up';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'BreadBasket';
  isAddUser = false;

  addUser(user: SignUp) {
    this.isAddUser = false;
  }

  addNewUser(user: SignUp ){
    this.isAddUser = true;

  }
}
<body>
  <header>
    <div class="topnav">
      <ul>
      <img class="logo-img" href="BreadBasket" src="../assets/Images/logonamu.png"  width="130" height="130" ALIGN=CENTER>
      <li><a routerLink="/sign-in-component">Sign In</a></li>
      <li><a routerLink="/sign-up-component">Sign Up</a></li>
      <li><a routerLink="/with-fillings-component">Bread</a></li>
      <li><a routerLink="/no-fillings-component">Dashboard</a></li>
      <!-- <a href="#Breads">Breads</a>
      <a href="#about">About</a> -->
      <a class="active" href="#home">Home</a>
    </ul>
    </div>
    </header>
</body>
