import { Component } from '@angular/core';
import { SignUp } from './sign-up';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'BreadBasket';
  isAddUser = false;

  addUser(user: SignUp) {
    this.isAddUser = false;
  }

  addNewUser(user: SignUp ){
    this.isAddUser = true;

  }
}
<body>
  <header>
    <div class="topnav">
      <ul>
      <img class="logo-img" src="../assets/Images/logonamu.png"  width="130" height="130" ALIGN=CENTER>
      <div class="dropdown">
        <button class="dropbtn">Dropdown
          <i class="fa fa-caret-down"></i>
        </button>
        <div class="dropdown-content">
          <a routerLink="/sign-in-component">Sign In</a>
          <a routerLink="/sign-up-component">Sign Up</a>
        </div>
      </div>
      <a routerLink="/with-fillings-component">With Fillings Bread</a>
      <a routerLink="/no-fillings-component">No Fillings Bread</a>
      <a class="active" routerLink="/home-component">Home</a>
    </ul>
    </div>
    </header>
</body>
    <router-outlet></router-outlet>
