import { Component, Output, EventEmitter } from '@angular/core';
import {FormControl, FormGroup} from '@angular/forms';

@Component({
  selector: 'app-sign-up',
  templateUrl: './sign-up.component.html',
  styleUrls: ['./sign-up.component.css']
})
export class SignUpComponent {
  ngOnInit() {
    this.signUpForm.reset();
  }
  @Output()userOut = new EventEmitter();
  signUpForm= new FormGroup({
    name: new FormControl(''),
    username: new FormControl(''),
    age: new FormControl(''),
    address: new FormControl(''),
    password: new FormControl('')
  })
  addUser() {
    this.userOut.emit(this.signUpForm.value);
  }

}
<body>
<div class="bg">
<br><br>
  <!-- <div *ngIf="!isAddUser"> -->
  <h2>Create Account</h2>


  <form action="signUpForm" (ngSubmit)="addUser" class="dv">
    <label for="name">Name</label>
    <br>
    <input id="name" type="text" formControlName="name">
    <br><br>
    <label for="username">User Name</label>
    <br>
    <input id="username" type="text" formControlName="username">
    <br><br>
    <label for="age">Age</label>
    <br>
    <input id="age" type="text" formControlName="age">
    <br><br>
    <label for="address">Address</label>
    <br>
    <input id="address" type="text" formControlName="address">
    <br><br>
    <label for="pwd">Password</label>
    <br>
    <input type="password" id="pwd" formControlName="pwd">
    <br><br>
</form>
  <br><br>
  <button>Sign Up</button>
  <br><br>
  <button >Sign In</button>
</div>

</body>
